import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Alert, Button, Container, Form, FormGroup, Input, Label } from "reactstrap";
import { api } from "../../../config";

export const EditarCli = (props) => {

    console.log(props.match.params.id)

    const [id] = useState(props.match.params.id)

    const [nome, setNome] = useState('');
    const [endereco, setEndereco] = useState('');
    const [cidade, setCidade] = useState('');
    const [uf, setUf] = useState('');
    const [nascimento, setNascimento] = useState('');
    const [clienteDesde, setClienteDesde] = useState('');

    const [status, setStatus] = useState({
        type: '',
        message: ''
    });

    const getCliente = async () => {
        await axios.get(api + "/cliente/" + id)
            .then((response) => {
                setNome(response.data.cli.nome)
                setEndereco(response.data.cli.endereco)
                setCidade(response.data.cli.cidade)
                setUf(response.data.cli.uf)
                setNascimento(response.data.cli.nascimento)
                setClienteDesde(response.data.cli.clienteDesde)
            })
            .catch(() => {
                setStatus({
                    type: 'error',
                    message: 'Erro: sem conexão com a API.'
                })
            });
    };

    const excluirCliente = async () => {

        const headers = {
            'Content-Type': 'application/json'
        };

        await axios.delete(api + "/excluircliente/" + id, { headers })
            .then((response) => {
                setStatus({
                    type: 'success',
                    message: response.data.message
                });
            })
            .catch(() => {
                setStatus({
                    type: "error",
                    message: "Erro: sem conexão com a API."
                })
            })
    }

    const editCliente = async e => {
        e.preventDefault();
        console.log("Editar")

        const headers = {
            'Content-Type': 'application/json'
        };

        await axios.put(api + "/cliente/" + id + "/editar", nome, endereco, cidade, uf, nascimento, clienteDesde, { headers })
            .then((response) => {
                setStatus({
                    type: 'success',
                    message: response.data.message
                });
            })
            .catch(() => {
                setStatus({
                    type: 'error',
                    message: "Sem conexão com a API."
                });
            });
    }

    useEffect(() => {
        getCliente();
    }, [])


    return (
        <div>
            <Container>
                <div className="d-flex">
                    <div>
                        <h1>Editar Cliente</h1>
                    </div>
                    <div className="m-auto p-2">
                        <Link to="/listar-cliente" className="btn btn-outline-primary btn-sm">
                            Cliente
                        </Link>
                    </div>
                </div>
                <hr className="m-1" />

                {status.type === 'error' ? <Alert color="danger">{status.message}</Alert> : ""}
                {status.type === 'success' ? <Alert color="success">{status.message}</Alert> : ""}

                <Form className="p-2" onSubmit={editCliente}>
                    <FormGroup className="p2">
                        <Label>
                            Nome
                        </Label>
                        <Input
                            name="nome"
                            placeholder="Nome do Cliente"
                            type="text"
                            value= {nome}
                            onChange={e =>setNome(e.target.value)}
                        />
                    </FormGroup>
                    <FormGroup>
                        <Label className="p-2">
                            Endereço
                        </Label>
                        <Input
                            name="endereco"
                            placeholder="Endereço do Cliente"
                            type="text"
                            value= {endereco}
                            onChange={e =>setEndereco(e.target.value)}
                        />
                    </FormGroup>
                    <FormGroup className="p2">
                        <Label>
                            Cidade
                        </Label>
                        <Input
                            name="cidade"
                            placeholder="Cidade do Cliente"
                            type="text"
                            value= {cidade}
                            onChange={e =>setCidade(e.target.value)}
                        />
                    </FormGroup>
                    <FormGroup className="p2">
                        <Label>
                            UF
                        </Label>
                        <Input
                            name="uf"
                            placeholder="UF"
                            type="text"
                            value= {uf}
                            onChange={e =>setUf(e.target.value)}
                        />
                    </FormGroup>
                    <FormGroup className="p2">
                        <Label>
                            Nascimento
                        </Label>
                        <Input
                            name="nascimento"
                            placeholder="Nascimento do Cliente"
                            type="date"
                            value= {nascimento}
                            onChange={e =>setNascimento(e.target.value)}
                        />
                    </FormGroup>
                    <FormGroup className="p2">
                        <Label>
                            Cliente Desde
                        </Label>
                        <Input
                            name="clienteDesde"
                            placeholder="Cliente Desde"
                            type="date"
                            value= {clienteDesde}
                            onChange={e =>setClienteDesde(e.target.value)}
                        />
                    </FormGroup>


                    <Button type="button" outline color="danger" onClick={excluirCliente}>
                        Excluir
                    </Button>

                    <Button type ="submit" outline color="success">
                        Salvar
                    </Button>

                </Form>

            </Container>
        </div>
    );
};
