import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Alert, Button, Container, Form, FormGroup, Input, Label } from "reactstrap";
import { api } from "../../../config";

export const EditarCli = (props) => {

    console.log(props.match.params.id)

    const [data, setData] = useState([]);

    const [id] = useState(props.match.params.id)

    const [status, setStatus] = useState({
        type: '',
        message: ''
    });

    useEffect(() => {
        const getCliente = async () => {
            await axios.get(api + "/cliente/" + id)
                .then((response) => {
                    console.log(response.data.cli)
                    setData(response.data.cli)
                })
                .catch(() => {
                    setStatus({
                        type: 'error',
                        message: 'Erro: sem conexão com a API.'
                    })
                });
        }
        getCliente();
    }, [id])


    return (
        <div>
            <Container>
                <div className="d-flex">
                    <div>
                        <h1>Editar Cliente</h1>
                    </div>
                    <div className="m-auto p-2">
                        <Link to="/listar-cliente" className="btn btn-outline-primary btn-sm">Cliente</Link>
                    </div>
                    {status.type === 'error' ?
                        <Alert color="danger">
                            {status.message}
                        </Alert> : ""}
                </div>

                <Form className="p-2">
                <FormGroup className="p2">
                    <Label>
                        Nome
                    </Label>
                    <Input
                        name="nome"
                        placeholder="Nome do Cliente"
                        type="text"
                        value= {data.nome}
                    />
                </FormGroup>
                <FormGroup>
                    <Label className="p-2">
                        Endereço
                    </Label>
                    <Input
                        name="endereco"
                        placeholder="Endereço do Cliente"
                        type="text"
                        value= {data.endereco}
                    />
                </FormGroup>
                <FormGroup className="p2">
                    <Label>
                        Cidade
                    </Label>
                    <Input
                        name="cidade"
                        placeholder="Cidade do Cliente"
                        type="text"
                        value= {data.cidade}
                    />
                </FormGroup>
                <FormGroup className="p2">
                    <Label>
                        UF
                    </Label>
                    <Input
                        name="uf"
                        placeholder="UF"
                        type="text"
                        value= {data.uf}
                    />
                </FormGroup>
                <FormGroup className="p2">
                    <Label>
                        Nascimento
                    </Label>
                    <Input
                        name="nascimento"
                        placeholder="Nascimento do Cliente"
                        type="date"
                        value= {data.nascimento}
                    />
                </FormGroup>
                <FormGroup className="p2">
                    <Label>
                        Cliente Desde
                    </Label>
                    <Input
                        name="clienteDesde"
                        placeholder="Cliente Desde"
                        type="date"
                        value= {data.clienteDesde}
                    />
                </FormGroup>

                <Button type="submit" outline color="success">
                    Editar
                </Button>
            </Form>
               
            </Container>
        </div>
    );
};
