import axios from "axios";
import { useState } from "react";
import { Link } from "react-router-dom"
import { Button, Container, Form, FormGroup, Input, Label } from "reactstrap"
import { api } from "../../../config";

export const CadastrarServ = () => {

    const cadServico = async  e =>{
        // console.log("Cadastrar");
        e.preventDefault();
        console.log(servico);

        const headers = {
            'Content-type': 'application/json'
        }

        await axios.post(api+"/servicos",servico,{headers})
        .then((response)=>{
            console.log(response.data.message)
        })
        .catch(()=>{
            console.log("Erro: Sem conexção com a API")
        })

    }
    const [servico, setServico] = useState({
        nome: '',
        descricao: ''
    })

    const valorInput = e => setServico({
        ...servico,[e.target.name]:e.target.value
    })

    return (
        <Container>
            <div className="d-flex">
                <div className="m-auto p-2">
                    <h1>Cadastrar Serviço</h1>
                </div>
                <div className="p-2">
                    <Link to="/listar-servico"
                        className="btn btn-outline-success btn-sm">Serviços</Link>
                </div>
            </div>

            <hr className="m-1"></hr>

            <Form className="p-2" onSubmit={cadServico}>
                <FormGroup className="p2">
                    <Label>
                        Nome
                    </Label>
                    <Input
                        name="nome"
                        placeholder="Nome do Serviço"
                        type="text"
                        onChange={valorInput}
                    />
                </FormGroup>
                <FormGroup>
                    <Label className="p-2">
                        Descrição
                    </Label>
                    <Input
                        name="descricao"
                        placeholder="Descrição do Serviço"
                        type="text"
                        onChange={valorInput}
                    />
                </FormGroup>
                
                <Button type="submit" outline color="success">
                    Cadastrar
                </Button>
            </Form>
        </Container>

    )
}
