import axios from "axios";
import { useEffect } from "react";
import { useState } from "react/cjs/react.development"
import { Container, Table } from "reactstrap"
import { api } from "../../../config";

export const PedidosCliente = (props) => {
    console.log(props.match.params.id)

    const [data, setData] = useState([]);

    const [id, setId] = useState(props.match.params.id);

    const [status, setStatus] = useState({
        type: '',
        message: ''
    });

    useEffect(()=>{
        const getPedidos = async() =>{
            await axios.get(api+"/clientes/"+id+"/pedidos")
            .then((response)=>{
                console.log(response.data.peds)
                setData(response.data.peds)
            })
            .catch(()=> {
                console.log("Sem conexão com a API!")
            });
        }
        getPedidos();
    }, [id])

    return (
        <div>
           <Container>
                <div>
                    <h1>Visualizar informações dos pedidos</h1>
                </div>
                {status.type == 'error' ?
                    <Alert color="danger">
                        {status.message}
                    </Alert> : ""}


                <Table striped>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Data</th>
                            <th>ClientId</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map(item => (
                            <tr key={item.id}>
                                <td>{item.id}</td>
                                <td>{item.data}</td>
                                <td>{item.ClienteId}</td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            </Container>
        </div>
    )
}
