import axios from "axios";
import { useState } from "react"
import { Link } from "react-router-dom";
import { Alert, Button, Container, Form, FormGroup, Input, Label } from "reactstrap";

export const EditarCliente = (props) => {

    const [data, setData] = useState([]);
    const [idCliente, setId] = useState([props.match.params.idCliente]);

    const [cliente, setIdCliente] = useState({
        nome: '',
        endereco: '',
        cidade: '',
        uf: '',
        nascimento: '',
        clienteDesde: ''
    });

    const [status, setStatus] = useState({
        type: '',
        message: ''
    });

    const editCliente = async e => {
        e.preventDefault();
        console.log("Editar")
    }

    const getClientes = async () => {
        await axios.get(api + "/listaclientes")
            .then((response) => {
                console.log(response.data.clientes);
                setData(response.data.clientes)
            })
            .catch(() => {
                setStatus({
                    type: 'error',
                    message: 'Erro: sem conexão com a API.'
                })
                // console.log("Sem conexão com a API")
            });
    };

    return (
        <div>
            <Container>
                <div className="d-flex">
                    <h1>Editar Cliente</h1>
                </div>
                <div className="p-2">
                    <Link to="/listar-cliente"
                        className="btn btn-outline-success btn-sm">
                        Cliente
                    </Link>
                </div>
                <hr className="m-1" />
                {status.type === 'success' ? <Alert color="success">{status.message}</Alert> : ''}
                {status.type === 'error' ? <Alert color="danger">{status.message}</Alert> : ''}

                <Form className="p-2" onSubmit={editCliente}>
                <FormGroup className="p2">
                    <Label>
                        Nome
                    </Label>
                    <Input
                        name="nome"
                        placeholder="Nome do Cliente"
                        type="text"
                        //onChange={valorInput}
                    />
                </FormGroup>
                <FormGroup>
                    <Label className="p-2">
                        Endereço
                    </Label>
                    <Input
                        name="endereco"
                        placeholder="Endereço do Cliente"
                        type="text"
                        //onChange={valorInput}
                    />
                </FormGroup>
                <FormGroup className="p2">
                    <Label>
                        Cidade
                    </Label>
                    <Input
                        name="cidade"
                        placeholder="Cidade do Cliente"
                        type="text"
                        //onChange={valorInput}
                    />
                </FormGroup>
                <FormGroup className="p2">
                    <Label>
                        UF
                    </Label>
                    <Input
                        name="uf"
                        placeholder="UF"
                        type="text"
                        //onChange={valorInput}
                    />
                </FormGroup>
                <FormGroup className="p2">
                    <Label>
                        Nascimento
                    </Label>
                    <Input
                        name="nascimento"
                        placeholder="Nascimento do Cliente"
                        type="date"
                        //onChange={valorInput}
                    />
                </FormGroup>
                <FormGroup className="p2">
                    <Label>
                        Cliente Desde
                    </Label>
                    <Input
                        name="clienteDesde"
                        placeholder="Cliente Desde"
                        type="date"
                        //onChange={valorInput}
                    />
                </FormGroup>

                <Button type="submit" outline color="success">
                    Editar
                </Button>
            </Form>
            </Container>
        </div>
    )
}
